<template>
  <div class="c-chart" :style="chartStyle" :id="id">
    <!--<v-chart :options="polar" />-->
  </div>
</template>
<script>
  import echarts from 'echarts'

  export default {
    name: '',
    data() {
      return {
        //生成一个随机的ID
        id: "main" + new Date().getMilliseconds() * Math.floor(Math.random() * 10000)
      }
    },
    props: {
      width: {
        type: Number,
        default: 0,
      },
      height: {
        type: Number,
        default: 300,
      },
      list: {
        type: Array,
        default() {
          let data = [];
          const names = ['周一', '周二', '周三', '周四', '周五', '周六', '周日'];

          for (let i = 0; i < names.length; i++) {
            data.push({
              name: names[i],
              value: Math.random() * 100,
            });
          }
          return data;
        },
      },

    },
    computed: {
      chartStyle() {
        return {
          width: this.width ? `${this.width}rem` : '100%',
          height: `${this.height}rem`,
        };
      },
    },
    mounted() {
      this.chartInit();
    },
    methods: {
      chartInit() {
        this.charts = echarts.init(document.getElementById(this.id));
        this.charts.setOption({
          xAxis: {
            type: 'category',
            name: '月',
            boundaryGap: false,
            axisLabel: {
              interval: 0,
              fontSize: '12'
            },
            data: this.list.map(item => ({
              value: item.name,
            })),
            axisLine: {
              lineStyle: {
                type: 'dashed',
                color: "#96A0C5",
              },
            },
            splitLine: {

              lineStyle: {
                type: 'dashed',

              },
            },
          },
          yAxis: {
            type: 'value',
            name: '万(㎡)',
            axisLabel: {
              formatter: (params) => {
                return params / 10000 + "万"
              },
            },
            axisTick: {
              show: false
            },
            axisLine: {
              show: false,
              fontSize: '12',
              lineStyle: {
                color: "#96A0C5",
              },
            },
            splitLine: {
              lineStyle: {
                type: 'dashed',
                color: ['#cccccc4d']
              },
            },
          },
          series: [{
            data: this.list.map(item => item.value),
            type: 'line',
            color: "#00E8FF",
            markPoint: {
              symbol: "image://data:image/png;base64,",
              data: [{
                name: '最大值',
                type: 'max'
              }],
              symbolOffset: ['30%', '-25%'],
              label: {
                fontSize: 12,
                color: '#0ADBDD'
              }
            },
            markLine: {

              symbol: 'none',//去掉箭头
              data: [{
                name: '最大值',
                type: 'max'
              }],
              label: {
                show: false
              }
            },
            itemStyle: {
              color: 'rgba(14,156,255,1)',
              normal: {
                lineStyle: {
                  width: 2,
                  type: 'solid',
                  color: new echarts.graphic.LinearGradient(0, 0, 0, 1, [{
                    offset: 0,
                    color: '#2ff5fa'
                  }, {
                    offset: 1,
                    color: '#0b7aa0'
                  }]),//线条渐变色
                }
              }
            },
          }],
          grid: {
            left: '15%',
            top: '20%',
            bottom: '10%',
            right: '15%'
          },
        });
      }
    }
  };
</script>
<style lang="scss">

  .c-chart {
    .echarts {
      width: 100%;
      height: 100%;
    }
  }
</style>
